mapper.select:
SELECT 

FROM oa_customer_eval_setting a

WHERE a.id = #{id}

SELECT 

FROM oa_customer_eval_setting a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
