mapper.select:
SELECT 

FROM oa_po_evaluate a

WHERE a.po_id = #{id}

SELECT 

FROM oa_po_evaluate a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM oa_po_evaluate a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE oa_po_evaluate SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
