mapper.select:
SELECT 

FROM oa_po_finance a

WHERE a.id = #{id}

SELECT 

FROM oa_po_finance a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.po_id = #{purchaseOrder.id}


		AND a.status = #{status}


		AND a.status >= #{minStatus}


		AND a.status  <= #{maxStatus}




		ORDER BY ${page.orderBy}


		ORDER BY a.po_id, a.sort ASC

SELECT 

FROM oa_po_finance a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC
