mapper.select:
SELECT 

FROM oa_stock_in a

WHERE a.id = #{id}

SELECT 

FROM oa_stock_in a


	a.del_flag = #{DEL_FLAG_NORMAL}

		And a.po_id = #{poId}


		And a.conract_id = #{contractId}


		And a.recall_id = #{recallId}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM oa_stock_in a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE oa_stock_in SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
